  # in order to pass any filters (e.g. authentication) defined in
  # SurveyAnswersController. Be sure to keep this updated too.
  let(:valid_session) { {} }

  describe 'GET #index' do
    it 'assigns all survey_answers as @survey_answers' do
      survey_answer = SurveyAnswer.create! valid_attributes
      get :index, session: valid_session
      expect(assigns(:survey_answers)).to eq([survey_answer])
    end
  end

  describe 'GET #show' do
    it 'assigns the requested survey_answer as @survey_answer' do
      survey_answer = SurveyAnswer.create! valid_attributes
      get :show, id: survey_answer.to_param, session: valid_session
