      it 'creates a new SurveyAnswer' do
        expect do
          post :create, survey_answer: valid_attributes, session: valid_session
        end.to change(SurveyAnswer, :count).by(1)
      end

      it 'assigns a newly created survey_answer as @survey_answer' do
        post :create, survey_answer: valid_attributes, session: valid_session
        expect(assigns(:survey_answer)).to be_a(SurveyAnswer)
